import React, { Component } from 'react';
import { withNamespaces } from 'react-i18next';
import Header from './components/Header';
import Hero from './components/Hero';
import Info from './components/Info';
import Program from './components/Program';
import Why from './components/Why';
import CTA from './components/CTA';
import Campaigns from './components/Campaigns';
import Supported from './components/Supported';
import Footer from './components/Footer';
import './i18n';

class App extends Component {
  state = {
    displayedTab: 1
  }
  // displayTab(index) {
  //   if(this.state.displayedTab != index) {
  //     this.setState({ displayedTab: index })
  //   }
  //   console.log('calling')
  // }
  //displayTab = (index) => this.setState({ displayedTab: index })
  displayTab = (index) => console.log(this.state.displayedTab, index.target)
  render() {
    console.log('render')
    console.log(this.state)
    return (
      <div>
        <Header t={this.props.t}/>
        <div className="tabs is-toggle is-fullwidth">
          <ul>
            <li className={this.state.displayedTab == 1 ? 'is-active' : ''}><a index={1} onClick={this.displayTab}>{this.props.t('tabs.title_1')}</a></li>
            <li className={this.state.displayedTab == 2 ? 'is-active' : ''}><a onClick={this.displayTab}>{this.props.t('tabs.title_2')}</a></li>
            <li className={this.state.displayedTab == 3 ? 'is-active' : ''}><a onClick={this.displayTab}>{this.props.t('tabs.title_3')}</a></li>
          </ul>
        </div>
        <div id="tab1">
          <Hero t={this.props.t}/>
          <Info t={this.props.t}/>
        </div>
        <div id="tab2" style={{ display: "none" }}>
          <Program t={this.props.t}/>
        </div>
        <div id="tab3" style={{ display: "none" }}>
          <Why t={this.props.t}/>
        </div>

        {/* <CTA t={this.props.t}/>
        <Campaigns t={this.props.t}/>
        <Supported t={this.props.t}/> */}
        <Footer t={this.props.t}/>
      </div>
    );
  }
}

export default withNamespaces()(App);
