import React, { Component } from 'react';
import { withNamespaces } from 'react-i18next';
import Header from './components/Header';
import Hero from './components/Hero';
import Info from './components/Info';
import Program from './components/Program';
import Why from './components/Why';
import CTA from './components/CTA';
import Campaigns from './components/Campaigns';
import Supported from './components/Supported';
import Footer from './components/Footer';
import './i18n';

class App extends Component {
  state = {
    displayedTab: 1
  }
  displayTab(index) {
    this.setState({ displayedTab: index })

  }
  render() {
    return (
      <div>
        <Header t={this.props.t}/>
        <div class="tabs is-toggle is-fullwidth">
          <ul>
            <li class={this.state.displayedTab == 1 ? 'is-active' : ''}><a onClick={displayTab(1)}>{this.props.t('tabs.title_1')}</a></li>
            <li class={this.state.displayedTab == 2 ? 'is-active' : ''}><a onClick={displayTab(2)}>{this.props.t('tabs.title_2')}</a></li>
            <li class={this.state.displayedTab == 3 ? 'is-active' : ''}><a onClick={displayTab(3)}>{this.props.t('tabs.title_3')}</a></li>
          </ul>
        </div>
        <div id="tab1">
          <Hero t={this.props.t}/>
          <Info t={this.props.t}/>
        </div>
        <div id="tab2" style={{ display: "none" }}>
          <Program t={this.props.t}/>
        </div>
        <div id="tab3" style={{ display: "none" }}>
          <Why t={this.props.t}/>
        </div>

        {/* <CTA t={this.props.t}/>
        <Campaigns t={this.props.t}/>
        <Supported t={this.props.t}/> */}
        <Footer t={this.props.t}/>
      </div>
    );
  }
}

export default withNamespaces()(App);
